package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

/**
 * user-modifiable class supporting dimension. * autogenerated from schema use
 * as a shell which can be edited
 * 
 */
public class CMLDimension extends AbstractDimension {

    /**
     * constructor.
     */
    public CMLDimension() {
    }

    /**
     * constructor.
     * 
     * @param old
     */
    public CMLDimension(CMLDimension old) {
        super((AbstractDimension) old);

    }

    /**
     * copy node .
     * 
     * @return Node
     */
    public Node copy() {
        return new CMLDimension(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     * 
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLDimension
     */
    public static CMLDimension makeElementInContext(Element parent) {
        return new CMLDimension();

    }

    /**
     * returns super.getPower(). enforces default value of 1.0 if attribute
     * missing.
     * 
     * @return the power (default = 1.0)
     */
    public double getPower() {
        if (this.getPowerAttribute() == null) {
            this.setPower(1.0);
        }
        return super.getPower();
    }
}
