package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting alternative. * autogenerated from schema use
 * as a shell which can be edited
 *
 */
public class CMLAlternative extends AbstractAlternative {

    /**
     * constructor.
     */
    public CMLAlternative() {
    }

    /**
     * constructor.
     *
     * @param old
     */
    public CMLAlternative(CMLAlternative old) {
        super((AbstractAlternative) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLAlternative(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent of element to be constructed (ignored by default)
     * @return CMLAlternative
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLAlternative();

    }
}
