package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting annotation. * autogenerated from schema use
 * as a shell which can be edited
 *
 */
public class CMLAnnotation extends AbstractAnnotation {

    /**
     * constructor.
     */
    public CMLAnnotation() {
    }

    /**
     * constructor.
     *
     * @param old
     */
    public CMLAnnotation(CMLAnnotation old) {
        super((AbstractAnnotation) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLAnnotation(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLAnnotation
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLAnnotation();

    }
}
