package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

/**
 * user-modifiable class supporting appinfo. * autogenerated from schema use as
 * a shell which can be edited
 * 
 */
public class CMLAppinfo extends AbstractAppinfo {

    /**
     * constructor.
     */
    public CMLAppinfo() {
    }

    /**
     * constructor.
     * 
     * @param old
     */
    public CMLAppinfo(CMLAppinfo old) {
        super((AbstractAppinfo) old);

    }

    /**
     * copy node .
     * 
     * @return Node
     */
    public Node copy() {
        return new CMLAppinfo(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     * 
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLAppinfo
     */
    public static CMLAppinfo makeElementInContext(Element parent) {
        return new CMLAppinfo();

    }
}
