package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting float. * autogenerated from schema use as a
 * shell which can be edited
 *
 */
public class CMLFloat extends AbstractFloat {

    /**
     * constructor.
     */
    public CMLFloat() {
    }

    /**
     * constructor.
     *
     * @param old
     */
    public CMLFloat(CMLFloat old) {
        super((AbstractFloat) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLFloat(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLFloat
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLFloat();

    }
}
