package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting floatArray. * autogenerated from schema use
 * as a shell which can be edited
 *
 */
public class CMLFloatArray extends AbstractFloatArray {

    /**
     * constructor.
     */
    public CMLFloatArray() {
    }

    /**
     * constructor.
     *
     * @param old
     */
    public CMLFloatArray(CMLFloatArray old) {
        super((AbstractFloatArray) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLFloatArray(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLFloatArray
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLFloatArray();

    }
}
