package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting stmml. * autogenerated from schema use as a
 * shell which can be edited
 *
 */
public class CMLStmml extends AbstractStmml {

    /**
     * contructor.
     */
    public CMLStmml() {
    }

    /**
     * contructor.
     *
     * @param old
     */
    public CMLStmml(CMLStmml old) {
        super((AbstractStmml) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLStmml(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLStmml
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLStmml();

    }
}
