package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

import org.xmlcml.cml.base.CMLElement;

/**
 * user-modifiable class supporting string. * autogenerated from schema use as a
 * shell which can be edited
 *
 */
public class CMLString extends AbstractString {

    /**
     * contructor.
     */
    public CMLString() {
    }

    /**
     * contructor.
     *
     * @param old
     */
    public CMLString(CMLString old) {
        super((AbstractString) old);

    }

    /**
     * copy node .
     *
     * @return Node
     */
    public Node copy() {
        return new CMLString(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     *
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLString
     */
    public CMLElement makeElementInContext(Element parent) {
        return new CMLString();

    }
}
