package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

/**
 * user-modifiable class supporting cml. * autogenerated from schema use as a
 * shell which can be edited
 * 
 */
public class CMLCml extends AbstractCml {

    /**
     * constructor.
     */
    public CMLCml() {
    }

    /**
     * constructor.
     * 
     * @param old
     */
    public CMLCml(CMLCml old) {
        super((AbstractCml) old);

    }

    /**
     * copy node .
     * 
     * @return Node
     */
    public Node copy() {
        return new CMLCml(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     * 
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLCml
     */
    public static CMLCml makeElementInContext(Element parent) {
        return new CMLCml();

    }
}
