package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

/**
 * user-modifiable class supporting integer. * autogenerated from schema use as
 * a shell which can be edited
 * 
 */
public class CMLInteger extends AbstractInteger {

    /**
     * constructor.
     */
    public CMLInteger() {
    }

    /**
     * constructor.
     * 
     * @param old
     */
    public CMLInteger(CMLInteger old) {
        super((AbstractInteger) old);

    }

    /**
     * copy node .
     * 
     * @return Node
     */
    public Node copy() {
        return new CMLInteger(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     * 
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLInteger
     */
    public static CMLInteger makeElementInContext(Element parent) {
        return new CMLInteger();

    }
}
