package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

/**
 * user-modifiable class supporting parameter. * autogenerated from schema use
 * as a shell which can be edited
 * 
 */
public class CMLParameter extends AbstractParameter {

    /**
     * constructor.
     */
    public CMLParameter() {
    }

    /**
     * constructor.
     * 
     * @param old
     */
    public CMLParameter(CMLParameter old) {
        super((AbstractParameter) old);

    }

    /**
     * copy node .
     * 
     * @return Node
     */
    public Node copy() {
        return new CMLParameter(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     * 
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLParameter
     */
    public static CMLParameter makeElementInContext(Element parent) {
        return new CMLParameter();

    }
}
