package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

/**
 * user-modifiable class supporting particle. * autogenerated from schema use as
 * a shell which can be edited
 * 
 */
public class CMLParticle extends AbstractParticle {

    /**
     * constructor.
     */
    public CMLParticle() {
    }

    /**
     * constructor.
     * 
     * @param old
     */
    public CMLParticle(CMLParticle old) {
        super((AbstractParticle) old);

    }

    /**
     * copy node .
     * 
     * @return Node
     */
    public Node copy() {
        return new CMLParticle(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     * 
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLParticle
     */
    public static CMLParticle makeElementInContext(Element parent) {
        return new CMLParticle();

    }
}
