package org.xmlcml.cml.element;

import nu.xom.Element;
import nu.xom.Node;

/**
 * user-modifiable class supporting system. * autogenerated from schema use as a
 * shell which can be edited
 * 
 */
public class CMLSystem extends AbstractSystem {

    /**
     * contructor.
     */
    public CMLSystem() {
    }

    /**
     * contructor.
     * 
     * @param old
     */
    public CMLSystem(CMLSystem old) {
        super((AbstractSystem) old);

    }

    /**
     * copy node .
     * 
     * @return Node
     */
    public Node copy() {
        return new CMLSystem(this);

    }

    /**
     * create new instance in context of parent, overridable by subclasses.
     * 
     * @param parent
     *            parent of element to be constructed (ignored by default)
     * @return CMLSystem
     */
    public static CMLSystem makeElementInContext(Element parent) {
        return new CMLSystem();

    }
}
